import React, { useState, useEffect } from 'react'
import { getQuestionsByCategory } from './api'
import { QuestionCard } from './QuestionCard'
import { Next } from './Next'

export const Game = (props) => {
  const { selectedCategory, handleBack } = props

  const [questByCat, setQuestByCat] = useState([])
  const [currentQuestIdx, setCurrentQuestIdx] = useState(0)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    getQuestionsByCategory(selectedCategory).then((data) => {
      setQuestByCat(data)
      setLoading(false)
    })
  }, [selectedCategory])

  return loading
    ? ('Questions Loading')
    : (
      <div>
        {questByCat.length > 0 && (
          <>
            <button onClick={handleBack}>Go Back to Categories</button>
            <h4>{selectedCategory.name} Trivia</h4>
            return (
            <QuestionCard
              question={questByCat[currentQuestIdx]}
            />
            <Next
              setCurrentQuestIdx={() =>
                setCurrentQuestIdx(currentQuestIdx + 1)}
            />
            )
          </>
        )}
      </div>
      )
}
