import React, { useState, useEffect } from 'react'
import he from 'he'
import { Answers } from './Answers'

export const QuestionCard = (props) => {
  const { questByCat } = props

  return (
    <div>
      <p>{he.decode(questByCat.question)}</p>
      <div>
        <ul>
          <Answers
            answerOpts={{
              correctAnswer: questByCat.correct_answer,
              wrongAnswer: questByCat.incorrect_answers
            }}
          />
        </ul>
      </div>
    </div>
  )
}
