import axios from 'axios'

export function getCategoryList () {
  return (
    axios
      .get('https://opentdb.com/api_category.php')
      .then(res => (res.data.trivia_categories))
  )
}

export function getQuestionsByCategory (category) {
  return (
    axios
      .get(`https://opentdb.com/api.php?amount=10&category=${category.id}&type=multiple`)
      .then((res) => {
        console.log(res.data.results)
        const data = res.data.results.map((categoryData) => ({
          category: categoryData.category,
          type: categoryData.type,
          difficulty: categoryData.difficulty,
          question: categoryData.question,
          correct_answer: categoryData.correct_answer,
          incorrect_answer: categoryData.incorrect_answer
        }))
      })
  )
}
