import React, { useState, useEffect } from 'react'
import { getQuestionsByCategory } from './api'
import he from 'he'
import { Question } from './Question'

export function QuestionsByCategory (props) {
  const [questionsByCategory, setQuestionsbyCategory] = useState([])
  const [loading, setLoading] = useState(true)


  const { selectedCategory } = props

  useEffect(() => {
    getQuestionsByCategory(selectedCategory).then((data) => {
      setQuestionsbyCategory(data)
      setLoading(false)
    })
  }, [selectedCategory])

  return loading
    ? 'Questions Loading'
    : (
      <div>
        <h4>{selectedCategory.name} Quiz</h4>
        <ul>
          {questionsByCategory.map((data) => {
            return (
              <Question
                key={questionsByCategory.idx}
                question={questionsByCategory.question}
                incorrect_answers={questionsByCategory.incorrect_answers}
                correct_answer={questionsByCategory.correct_answer}
              />
            )
          })}
        </ul>
      </div>
      )
}
