import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './App.css'

const App = () => {
  const [quiz, setQuiz] = useState([])
  useEffect(() => 
    axios.get('https://opentdb.com/api.php?amount=10&type=multiple')
      .then(res => setQuiz(res.results)))

  return (
    <div class='container'>
      <h1>Welcome to Trivia Night</h1>
      <h2>Grab a Drink and Select a Category to Begin</h2>
      <div class='categories'>
        {quiz.map((category, idx) => {
          return (
            <QuestionCard category={quiz.category} key={idx} id={idx} />
          )
        })}
      </div>
    </div>
  )
}

export const QuestionCard = (props) => {
  const { id, category, type, difficulty, question, correct_answer, incorrect_answers } = props
}

export default App
