import he from 'he'
import { QuestionsByCategory } from './QuestionsByCategory'

export function Question (props) {
  const [correct, setCorrect] = useState(null)
  const { question, incorrect_answers, correct_answer } = props

  return (
    <div key={question.idx}>
      <p>{he.decode(question.questionsByCategory.question)}?</p>
      <p>{he.decode(question.questionsByCategory.data.incorrect_answers)}</p>
    </div>
  )
}
